import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

// form for adding/editing tasks to the list of 10
export default function TaskForm(props:{tasks:string[], updateTasks:Function}){
  let tasks:string[] = [];
  if(props.tasks){
    props.tasks.forEach((task:string) => {
      tasks.push(task);
    });
  }
  if(tasks.length !== 10){
    for(let i = tasks.length; i < 10; i++){
      tasks[i] = "";
    }
  }
  return(
    <Form>
      {tasks.map((task, index)=>
        <TaskGroup task={task} index={index} />
      )}
      <Button variant="primary" onClick={()=>props.updateTasks(tasks)}>Submit</Button>
    </Form>
  );
}

function TaskGroup(props:{task:string, index:number}){
  return(
    <Form.Group controlId={props.index + "-" + props.task}>
      <div style={taskRow}>
        <Form.Label style={{width: "40px", marginTop: "5px"}}>#{props.index+1}</Form.Label>
        <Form.Control type="text" value={props.task} />
      </div>
    </Form.Group>
  )
}

let taskRow = {
  display: "flex"
}
