import React from 'react';
import Navigation from './nav/Nav';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import ListGroup from 'react-bootstrap/ListGroup';

export default class App extends React.Component<any,any> {
  constructor(props:any){
    super(props);
    this.state = {
      section: "home"
    };
    this.setSection = this.setSection.bind(this);
  }

  setSection(section:string) {
    this.setState({
      section:section
    });
  }

  render(){
    return(
      <>
        <Navigation
          current="Current" 
          bg="primary" 
          textTheme="dark"
          setSection={this.setSection} />
        { this.state.section === "home" &&
          <Container fluid={true} style={ pageBg }>
            <Card>Home section
            </Card>
          </Container>
        }
        { this.state.section === "view" &&
          <Container fluid={true} style={ pageBg }>
            <Card>View section</Card>
          </Container>
        }
        { this.state.section === "settings" &&
          <Container fluid={true} style={ pageBg }>
            <Card>
              <Card.Body>
                <Card.Title>Colors &amp; Patterns</Card.Title>

              </Card.Body>
            </Card>
            <Card>
              <Card.Body>
                <Card.Title>Chart Formats</Card.Title>

              </Card.Body>
            </Card>
            <Card>
              <Card.Body>
                <Card.Title>Tasks</Card.Title>

              </Card.Body>
            </Card>
            <Card>
              <Card.Body>
                <Card.Title>Page Theme</Card.Title>
                <ListGroup>
                  <ListGroup.Item active>Light</ListGroup.Item>
                  <ListGroup.Item disabled>Dark</ListGroup.Item>
                </ListGroup>
              </Card.Body>
            </Card>
          </Container>
        }
      </>
    )
  }
}

const pageBg = {
  backgroundColor: "lightgrey",
  height: "100vh",
  padding: "20px",
  paddingTop: "76px"
};
