import React from 'react';
import Form from 'react-bootstrap/Form';

// form for adding/editing tasks to the list of 10
export default function TaskForm(props:any){
  let tasks:string[] = [];
  if(props.tasks){
    props.tasks.forEach((task:string) => {
      tasks.push(task);
    });
  }
  return(
    <Form>
  {tasks.map((task, index)=>{
    <>
      <Form.Label>#{index}</Form.Label>
      <Form.Control type="text" value={task} />
    </>
  })}
    </Form>
  );
}
