import React from 'react';
import Navigation from './components/Nav';
import Settings from './components/Settings';
import Home from './components/Home';
import Chart from './components/Chart';
import iDB from './data/indexedDb';

export default class App extends React.Component<any,any> {
  constructor(props:any){
    super(props);
    this.state = {
      section: "home",
      tasks: ["", "", "", "", "", "", "", "", "", ""],
      entries: []
    };
    this.setSection = this.setSection.bind(this);
    this.updateTasks = this.updateTasks.bind(this);
    this.addEntry = this.addEntry.bind(this);
  }

  componentDidMount() {
    iDB.init("10for20", 1, ["tasks", "entries"])
    .then((message)=>{
      iDB.getAll("10for20", 1, "tasks")
      .then((tasks)=>{
        this.updateTasks(tasks)
        iDB.getAll("10for20", 1, "entries")
        .then((entries)=>{
          if(Array.isArray(entries)){
            entries.forEach((entry)=>{
              this.addEntry(entry);
            });
          }
        }).catch((message:string)=>{
          console.error(message);
        })
      }).catch((message:string)=>{
        console.error(message);
      });
    }).catch((message:string) => {
      console.error(message);
    });
  }

  setSection(section:string) {
    this.setState({
      section:section
    });
  }

  updateTasks(tasks:any){
    this.setState({
      tasks: tasks
    });
  }

  addEntry(entry:any){
    let entries = this.state.entries;
    entries.push(entry);
    this.setState({
      entries: entries
    });
  }

  render(){
    return(
      <>
        <Navigation
          current="Current" 
          bg="light" 
          textTheme="light"
          setSection={this.setSection} />
        { this.state.section === "home" &&
          <Home style={pageBg} tasks={this.state.tasks} updateTasks={this.updateTasks} updateEntries={this.addEntry} />
        }
        { this.state.section === "chart" &&
          <Chart style={pageBg} tasks={this.state.tasks} entries={this.state.entries} updateTasks={this.updateTasks} />
        }
        { this.state.section === "settings" &&
          <Settings style={pageBg} tasks={this.state.tasks} />
        }
      </>
    )
  }
}

const pageBg = {
  backgroundColor: "lightgrey",
  height: "100vh",
  padding: "20px",
  paddingTop: "76px"
};
