import React from 'react';
import Form from 'react-bootstrap/Form';

// form for adding/editing tasks to the list of 10
export default function TaskForm(props:any){
  let tasks:string[] = [];
  if(props.tasks){
    props.tasks.forEach((task:string) => {
      tasks.push(task);
    });
  }
  return(
    <Form>
      {tasks.map((task, index)=>{
        <TaskGroup task={task} index={index} />
      })}
    </Form>
  );
}

function TaskGroup(props:{task:string, index:number}){
  return(
    <Form.Group controlId={props.index + "-" + props.task}>
      <Form.Label>#{props.index}</Form.Label>
      <Form.Control type="text" value={props.task} />
    </Form.Group>
  )
}
