import React from 'react';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';

export default function EntryForm(props:{tasks:string[], updateTasks:Function}){
  return(
    <Form>
      {/** Date Picker */}
      <Form.Group controlId="date-pick">
        <Form.Row>
          <Col>
            <Form.Label>Date</Form.Label>
          </Col>
          <Col>
            <Form.Control as="input" type="date"></Form.Control>
          </Col>
        </Form.Row>
      </Form.Group>
      {/** Time Spent (TODO) */}

      {/** Task Completed (select) */}
      <Form.Group controlId="task-pick">
        <Form.Row>
          <Col>
            <Form.Label>Task Completed</Form.Label>
          </Col>
          <Col>
            <Form.Control as="select">
              {props.tasks.map((task)=>
                <option value={task}>{task}</option>
              )}
            </Form.Control>
          </Col>
        </Form.Row>
      </Form.Group>
      <Button variant="primary">Submit</Button>
    </Form>
  );
}
